import React, { useState, useEffect } from "react";

function Home(props) {
  const [posts, setPosts] = useState([])
  useEffect(() =>{
   // fetch('/api/2560796474159112/1').then(res => res.json()).then(res => console.log(res));
    fetch('http://localhost:8000/wp-json/wp/v2/posts').then(res => res.json()).then(res => setPosts(res))
  })
  return (
    <div className="home">
      <h1>Home</h1>
      {posts.map(post => {
        <div>
          <p>{post.title.rendered}</p>
        </div>
      })}
    </div>
  );
}

export default Home;
